{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"name":"codigo.py","provenance":[],"private_outputs":true,"collapsed_sections":[]},"kernelspec":{"name":"python3","display_name":"Python 3"}},"cells":[{"cell_type":"code","metadata":{"id":"glrDl0M0YbZ_","colab_type":"code","colab":{}},"source":["import numpy\n","import math\n","import matplotlib.pyplot as plt\n","\n","def calcr(lamb,y0):\n","  r=lamb*y0/100000\n","  #print (r)\n","  return r\n","\n","def calclamb(beta,y0):\n","  lamb=beta*y0/100\n","  #print (lamb)\n","  return lamb\n","\n","def eulerEDO(h,y0,t0,x,r,lamb):\n","  yn=0.\n","  yn=y0\n","  tn=t0\n","  if(x!=0):\n","    while(tn<=x):\n","      yn = yn + h*yn*(lamb - r) #Função: dy/dt = yn(lambda-r)\n","      tn+=h\n","  return yn\n","\n","def rungeKutta4(h,y0,t0,x,r,lamb):\n","  yn=0.\n","  yn=y0\n","  tn=t0\n","  s1=0.\n","  s2=0.\n","  s3=0.\n","  s4=0.\n","  if(x!=0):\n","    while(tn<=x):\n","      s1=yn*(lamb-r) #Função: dy/dt = yn(lambda-r)\n","      s2=(yn+s1*h/2)*(lamb-r) #Função: dy/dt = yn(lambda-r),  para yn = yn+s1*h/2\n","      s3=(yn+s2*h/2)*(lamb-r)  #Função: dy/dt = yn(lambda-r)  para yn = yn+s2*h/2\n","      s4=(yn+s3*h)*(lamb-r)  #Função: dy/dt = yn(lambda-r), para yn = yn+s3*h\n","      yn = yn + (h/6)*(s1+2*s2+2*s3+s4)\n","      tn+=h\n","  return yn\n","\n","t0=0\n","y0=5660 #Quantidade de pessoas confirmadas com sarampo EM 3 MESES!!!!\n","h=0.1\n","x=1\n","beta=73/100000\n","lamb=calclamb(beta,y0)  \n","r=calcr(lamb,y0)\n","R=beta*y0*lamb\n","print(\"O valor do R0 para a determinação epidêmica será\", R)\n","\n","\n","xP = []\n","yPeuler = []\n","yPkutta = []\n","for i in range(4): #Número de períodos projetados (1 ano)\n","  xP.append(i+1)\n","  eu = eulerEDO(h,y0,t0,(i+1),r,lamb)\n","  yPeuler.append(eulerEDO(h,y0,t0,(i+1),r,lamb))\n","  yPkutta.append(rungeKutta4(h,y0,t0,(i+1),r,lamb))\n","\n","  print(\"A quantidade de pessoas infectadas em \" + str(i+1) + \" trimestres será(De acordo com o Método de Euler) de\", eulerEDO(h,y0,t0,(i+1),r,lamb)) \n","  print(\"A quantidade de pessoas infectadas em \" + str(i+1) + \" trimestres será(De acordo com o Método de Kutta de Quarta Ordem) de\", rungeKutta4(h,y0,t0,(i+1),r,lamb))\n","  print(\"\\n\")\n","\n","  y0 = eulerEDO(h,y0,t0,i,r,lamb)\n","\n","x1 = xP\n","x2 = xP\n","\n","y1 = yPeuler\n","y2 = yPkutta\n","\n","plt.subplot(2, 1, 1)\n","plt.plot(x1, y1, label = 'Método de euler')\n","plt.title('Evolução do número de infecções de sarampo')\n","plt.xlabel('Trimestres')\n","plt.ylabel('Número de infectados')\n","plt.legend()\n","\n","plt.subplot(2, 1, 2)\n","plt.plot(x2, y2, label = 'Método de Kutta', color = 'r')\n","plt.xlabel('Trimestres')\n","plt.ylabel('Número de infectados')\n","plt.legend()\n","\n","plt.savefig(\"grafico.png\")\n"],"execution_count":0,"outputs":[]}]}